mapper.update:
UPDATE common_code SET name = #{name},
                       sort = #{sort},
                       used_flag = #{usedFlag}
WHERE deleted_flag = false AND
      uid = #{uid}

UPDATE common_code SET image = #{image}
WHERE deleted_flag = false AND
      uid = #{uid}

UPDATE common_code SET used_flag = #{usedFlag}
WHERE deleted_flag = false AND
      uid = #{uid}

mapper.delete:
UPDATE common_code SET deleted_flag = #{deletedFlag}
WHERE deleted_flag = false AND
      uid = #{uid}

mapper.select:
SELECT COUNT(*) AS cnt
FROM common_code
WHERE deleted_flag = false AND
      name = #{name}

  AND parent_common_code_uid = #{parentCommonCodeUid}

SELECT COUNT(*) AS cnt
FROM common_code
WHERE deleted_flag = false

  AND parent_common_code_uid = #{request.commonCodeUid}


  AND used_flag = #{request.usedFlag}


  AND name LIKE CONCAT('%', #{request.name}, '%')

SELECT uid, created_at, updated_at, deleted_flag, used_flag, case when #{request.lang} IS NULL OR #{request.lang} = '' OR #{request.lang} = 'en' THEN name ELSE name_kr END AS name, image, sort, parent_common_code_uid FROM common_code WHERE deleted_flag = false
AND parent_common_code_uid = #{request.commonCodeUid}
AND used_flag = #{request.usedFlag}
AND name LIKE CONCAT('%', #{request.name}, '%')
ORDER BY sort ASC
