mapper.select:
SELECT COUNT(*) FROM realestate.item_buy AS ib LEFT OUTER JOIN realestate.item AS i ON ib.item_uid = i.uid AND i.deleted_flag = false AND i.used_flag = true LEFT OUTER JOIN realestate.`member` m ON i.member_uid = m.uid AND m.deleted_flag = false AND m.used_flag = true LEFT OUTER JOIN realestate.exchange usd ON usd.code = 'FRX.KRWUSD' AND usd.deleted_flag = false AND usd.used_flag = true LEFT OUTER JOIN realestate.exchange_virtual2 usdt on usdt.name = 'FOGnet_USD' AND usdt.deleted_flag = false AND usdt.used_flag = true LEFT OUTER JOIN common_code AS acc ON i.allocation_uid = acc.uid AND acc.deleted_flag = false AND acc.used_flag = true LEFT OUTER JOIN common_code AS scc ON i.status_uid = scc.uid AND scc.deleted_flag = false AND scc.used_flag = true WHERE ib.deleted_flag = false AND ib.used_flag = true AND i.current_quantity > 0 AND ib.market_item_uid = #{request.marketItemUid}
AND i.member_uid = #{request.memberUid}
AND m.wallet_address LIKE CONCAT('%',#{request.searchValue},'%')
AND ib.created_at BETWEEN #{request.startDate} AND #{request.endDate}

SELECT ib.uid, ib.created_at, ib.updated_at, ib.deleted_flag, ib.used_flag,
ib.item_uid, ib.market_item_uid, i.quantity, i.current_quantity, ib.price, ib.price / usd.base_price AS usd_price, ib.price / usd.base_price / fog.price AS fog_price, ib.nft_id, ib.contract_address, CONCAT('#', ib.index) AS index_name , i.member_uid , i.allocation_uid , acc.name AS allocation_name, i.status_uid , scc.name AS status_name, i.image_uri , i.project_name, i.total_price, i.total_price / usd.base_price AS usd_total_price, i.total_price / usd.base_price / fog.price AS fog_total_price, i.quantity, i.current_quantity, i.allocation_day, i.`right`, i.location, i.core_value, i.area, i.`scale`, i.purpose, i.company_name, i.approval_at, i.website_uri, i.detail, i.token_uri, m.wallet_address, mi.start_at, mi.end_at FROM realestate.item_buy AS ib LEFT OUTER JOIN realestate.item AS i ON ib.item_uid = i.uid AND i.deleted_flag = false AND i.used_flag = true LEFT OUTER JOIN realestate.`member` AS m ON i.member_uid = m.uid AND m.deleted_flag = false AND m.used_flag = true LEFT OUTER JOIN realestate.market_item AS mi ON ib.market_item_uid = mi.uid AND mi.deleted_flag = false AND mi.used_flag = true LEFT OUTER JOIN realestate.exchange usd ON usd.code = 'FRX.KRWUSD' AND usd.deleted_flag = false AND usd.used_flag = true LEFT OUTER JOIN realestate.exchange_virtual2 fog on fog.name = 'FOGnet_USD' AND fog.deleted_flag = false AND fog.used_flag = true LEFT OUTER JOIN common_code AS acc ON i.allocation_uid = acc.uid AND acc.deleted_flag = false AND acc.used_flag = true LEFT OUTER JOIN common_code AS scc ON i.status_uid = scc.uid AND scc.deleted_flag = false AND scc.used_flag = true WHERE ib.deleted_flag = false AND ib.used_flag = true AND i.current_quantity > 0 AND ib.market_item_uid = #{request.marketItemUid}
AND i.member_uid = #{request.memberUid}
AND m.wallet_address LIKE CONCAT('%',#{request.searchValue},'%')
AND ib.created_at BETWEEN #{request.startDate} AND #{request.endDate}
ORDER BY ib.uid DESC LIMIT #{request.offsetAndRowCount.offset}, #{request.offsetAndRowCount.rowCount}

SELECT COUNT(*) FROM realestate.item_buy AS ib LEFT OUTER JOIN realestate.item AS i ON ib.item_uid = i.uid AND i.deleted_flag = false AND i.used_flag = true LEFT OUTER JOIN realestate.`member` m ON i.member_uid = m.uid AND m.deleted_flag = false AND m.used_flag = true LEFT OUTER JOIN realestate.exchange usd ON usd.code = 'FRX.KRWUSD' AND usd.deleted_flag = false AND usd.used_flag = true LEFT OUTER JOIN realestate.exchange_virtual2 usdt on usdt.name = 'FOGnet_USD' AND usdt.deleted_flag = false AND usdt.used_flag = true LEFT OUTER JOIN common_code AS acc ON i.allocation_uid = acc.uid AND acc.deleted_flag = false AND acc.used_flag = true LEFT OUTER JOIN common_code AS scc ON i.status_uid = scc.uid AND scc.deleted_flag = false AND scc.used_flag = true WHERE ib.deleted_flag = false AND ib.used_flag = true AND i.current_quantity > 0
AND i.member_uid = #{request.memberUid}
AND m.wallet_address LIKE CONCAT('%',#{request.searchValue},'%')

SELECT ib.uid, ib.created_at, ib.updated_at, ib.deleted_flag, ib.used_flag,
ib.item_uid, ib.market_item_uid, i.quantity, i.current_quantity, ib.price, ib.price / usd.base_price AS usd_price, ib.price / usd.base_price / fog.price AS fog_price, ib.nft_id, ib.contract_address, CONCAT('#', ib.index) AS index_name , i.member_uid , i.allocation_uid , acc.name AS allocation_name, i.status_uid , scc.name AS status_name, i.image_uri , i.project_name, i.total_price, i.total_price / usd.base_price AS usd_total_price, i.total_price / usd.base_price / fog.price AS fog_total_price, i.quantity, i.current_quantity, i.allocation_day, i.`right`, i.location, i.core_value, i.area, i.`scale`, i.purpose, i.company_name, i.approval_at, i.website_uri, i.detail, i.token_uri, m.wallet_address, mi.start_at, mi.end_at, mi.transaction_hash FROM realestate.item_buy AS ib LEFT OUTER JOIN realestate.item AS i ON ib.item_uid = i.uid AND i.deleted_flag = false AND i.used_flag = true LEFT OUTER JOIN realestate.`member` AS m ON i.member_uid = m.uid AND m.deleted_flag = false AND m.used_flag = true LEFT OUTER JOIN realestate.market_item AS mi ON ib.market_item_uid = mi.uid AND mi.deleted_flag = false AND mi.used_flag = true LEFT OUTER JOIN realestate.exchange usd ON usd.code = 'FRX.KRWUSD' AND usd.deleted_flag = false AND usd.used_flag = true LEFT OUTER JOIN realestate.exchange_virtual2 fog on fog.name = 'FOGnet_USD' AND fog.deleted_flag = false AND fog.used_flag = true LEFT OUTER JOIN common_code AS acc ON i.allocation_uid = acc.uid AND acc.deleted_flag = false AND acc.used_flag = true LEFT OUTER JOIN common_code AS scc ON i.status_uid = scc.uid AND scc.deleted_flag = false AND scc.used_flag = true WHERE ib.deleted_flag = false AND ib.used_flag = true AND i.current_quantity > 0
AND i.member_uid = #{request.memberUid}
AND m.wallet_address LIKE CONCAT('%',#{request.searchValue},'%')
ORDER BY ib.uid DESC LIMIT #{request.offsetAndRowCount.offset}, #{request.offsetAndRowCount.rowCount}
