mapper.select:
SELECT 	if.uid,
        if.created_at,
        if.updated_at,
        if.deleted_flag,
        if.used_flag,
        if.category_uid,
        if.item_uid,
        if.file_uri
FROM	realestate.item_file AS if
        LEFT OUTER JOIN LEFT OUTER JOIN common_code AS cc
ON if.category_uid  = cc.uid AND cc.deleted_flag = false AND cc.used_flag = true
WHERE	if.used_flag = true AND
  if.deleted_flag = false AND
  if.item_uid = #{itemUid}

mapper.update:
UPDATE realestate.market_item
SET current_quantity = current_quantity - #{currentQuantity}
WHERE uid = #{uid}
