mapper.update:
UPDATE role SET name = #{name},
                             used_flag = #{usedFlag}
WHERE deleted_flag = false AND
      uid = #{uid}

mapper.delete:
UPDATE role SET deleted_flag = #{deletedFlag}
WHERE deleted_flag = false AND
      uid = #{adminUid}

mapper.select:
SELECT uid, created_at, updated_at, deleted_flag, used_flag, name, description FROM role WHERE deleted_flag = false
AND used_flag = #{request.usedFlag}
ORDER BY uid DESC
